import React from 'react';
import FieldAffix from '../private/FieldAffix';
/** A prefix to display before an input within a form field. */

export default class Prefix extends React.PureComponent {
  render() {
    const {
      children,
      compact,
      disabled
    } = this.props;
    return React.createElement(FieldAffix, {
      before: true,
      compact: compact,
      disabled: disabled
    }, children);
  }

}