import React from 'react';
import FieldAffix from '../private/FieldAffix';
/** A suffix to display after an input within a form field. */

export default class Suffix extends React.PureComponent {
  render() {
    const {
      children,
      compact,
      disabled
    } = this.props;
    return React.createElement(FieldAffix, {
      after: true,
      compact: compact,
      disabled: disabled
    }, children);
  }

}