import T from '../Translate';
export default function defaultFormatter(chosen, getLabel) {
  if (chosen.length > 0) {
    const labels = chosen.map((_, i, a) => getLabel(a.slice(0, i + 1)));
    return labels.join(' > ');
  }

  return T.phrase('Select from hierarchy', {}, {
    context: 'Placeholder text for HierarchyPicker',
    key: 'lunar.picker.selectPlaceholder'
  });
}